import React from 'react';
import letters from './letters.jpg';

const Contact = () => {

    const onSubmitMessage = async () => {
        e.preventDefault();

        const api_key = 'b2f854725c7c288d5b254573a511401b-65b08458-3e21f106';
        const mailgun = require("mailgun-js");
        const DOMAIN = 'sandbox436c1cd3882e4a22814d606900fc7bc5.mailgun.org';
        const mg = mailgun({apiKey: api_key, domain: DOMAIN});
        const data = {
            from: 'Excited User <rancellta@gmail.com>',
            to: 'rancellta@gmail.com',
            subject: 'Hello',
            text: 'Testing some Mailgun awesomness!'
        };
        mg.messages().send(data, function (error, body) {
            if (error) {
                console.log(error);
            }
            console.log(body);
        });

        // var API_KEY = 'b2f854725c7c288d5b254573a511401b-65b08458-3e21f106';
        // var DOMAIN = 'sandbox436c1cd3882e4a22814d606900fc7bc5.mailgun.org';
        // var mailgun = require('mailgun-js')({apiKey: API_KEY, domain: DOMAIN});
        const url = 'https://api.mailgun.net/v3/sandbox436c1cd3882e4a22814d606900fc7bc5.mailgun.org'

        // const data = {
        // from: 'Excited User <rancellta@gmail.com>',
        // to: 'rancellta@gmail.com, bar@example.com',
        // subject: 'Hello',
        // text: 'Testing some Mailgun awesomeness!'
        // };

        const respuesta = await fetch(url);
        const resultado = await respuesta.json();

        mailgun.messages().send(data, (error, body) => {
            if (error) {
                console.log(error);
            }
            console.log(body);
        });
    }



    return ( 

        <div className = "container">
            <div className = "ml-4 mr-4">
                <div className = "row">
                    <div className = "col-xl-12  col-lg-12 col-md-12 col-sm-12 mt-4">
                        <img src = {letters} 
                            className = "rounded border border-white letters"
                            alt = ""
                        />  
                    </div>

                    <div className = "col-xl-12 col-lg-12 col-md-12 col-sm-12 ">
                        <div className = "bg-light rounded  mt-4"> 
                            <hr/>
                            <h1 className = " ml-3 mt-4 font-title"> Contact us </h1>
                            <hr className = "bg-black"/>

                            <form
                                onSubmit = {onSubmitMessage}
                            >
                                <div className = "container">
                                    <div className = "form-group">
                                        <label className = "font-body">Full Name</label>
                                        <input 
                                            type = "name" 
                                            className = "form-control" 
                                            id = "name" 
                                            placeholder = "Your Full Name"
                                            style = {{width: "40%"}}
                                        />
                                    </div>

                                    <div className = "form-group">
                                        <label className = "font-body">Email</label>
                                        <input
                                            type = "email"
                                            className = "form-control"
                                            id = "email"
                                            placeholder = "name@example.com"
                                            style = {{width: "40%"}}
                                        />
                                    </div>

                                    <div className = "form-group">
                                        <textarea className = "form-control" rows = "5" placeholder = "Your message"/>
                                    </div>

                                    <div className = "form-group">
                                        <button type="submit" class="btn btn-primary">Send Email</button>  
                                    </div>

                                    <hr/>
                                </div>
                            </form>
                        </div> 
                    </div>
                </div>
            </div>

        </div> 
     );
}

export default Contact;
